def test_magic(f, magic_hex):
    """ tests a given file to see if the magic hex matches.
    """
    data = f.read(len(magic_hex))
    if len(data) != len(magic_hex):
        return 0
    for i in range(len(magic_hex)):
        if magic_hex[i] != ord_(data[i]):
            return 0
    return 1

